import React, { useEffect, useState } from 'react';
import axios from 'axios';

interface Film {
    url: string;
    title: string;
}

interface Character {
    url: string;
    name: string;
}

interface CharacterDetails {
    name: string;
    height: string;
    mass: string;
    hair_color: string;
    skin_color: string;
    eye_color: string;
    birth_year: string;
    gender: string;
}

function App() {
    const [films, setFilms] = useState<Film[]>([]);
    const [selectedFilm, setSelectedFilm] = useState<Film | null>(null);
    const [characters, setCharacters] = useState<Character[]>([]);
    const [selectedCharacter, setSelectedCharacter] = useState<Character | null>(null);
    const [characterDetails, setCharacterDetails] = useState<CharacterDetails | null>(null);

    useEffect(() => {
        fetchFilms();
    }, []);

    const fetchFilms = async () => {
        try {
            const response = await axios.get<{ results: Film[] }>('/api/swapi/films');
            setFilms(response.data.results);
        } catch (error) {
            console.error(error);
        }
    };

    const fetchCharacters = async (filmUrl: string) => {
        try {
            const response = await axios.get<{ characters: string[] }>(filmUrl);
            const characterUrls = response.data.characters;
            const charactersData = await Promise.all(
                characterUrls.map(async (characterUrl) => {
                    const response = await axios.get<Character>(characterUrl);
                    return response.data;
                })
            );
            setCharacters(charactersData);
        } catch (error) {
            console.error(error);
        }
    };

    const fetchCharacterDetails = async (characterUrl: string) => {
        try {
            const response = await axios.get<CharacterDetails>(characterUrl);
            setCharacterDetails(response.data);
        } catch (error) {
            console.error(error);
        }
    };

    const handleFilmSelect = (film: Film) => {
        setSelectedFilm(film);
        setSelectedCharacter(null);
        setCharacterDetails(null);
        fetchCharacters(film.url);
    };

    const handleCharacterSelect = (character: Character) => {
        setSelectedCharacter(character);
        fetchCharacterDetails(character.url);
    };

    return (
        <div>
            <h1>Star Wars Films</h1>
            <ul>
                {films.map((film) => (
                    <li key={film.url}>
                        <button onClick={() => handleFilmSelect(film)}>{film.title}</button>
                    </li>
                ))}
            </ul>

            {selectedFilm && (
                <div>
                    <h2>Characters in {selectedFilm.title}</h2>
                    <ul>
                        {characters.map((character) => (
                            <li key={character.url}>
                                <button onClick={() => handleCharacterSelect(character)}>{character.name}</button>
                            </li>
                        ))}
                    </ul>
                </div>
            )}

            {selectedCharacter && characterDetails && (
                <div>
                    <h2>Character Details</h2>
                    <p>Name: {characterDetails.name}</p>
                    <p>Height: {characterDetails.height}</p>
                    <p>Mass: {characterDetails.mass}</p>
                    <p>Hair Color: {characterDetails.hair_color}</p>
                    <p>Skin Color: {characterDetails.skin_color}</p>
                    <p>Eye Color: {characterDetails.eye_color}</p>
                    <p>Birth Year: {characterDetails.birth_year}</p>
                    <p>Gender: {characterDetails.gender}</p>
                </div>
            )}
        </div>
    );
}

export default App;
