import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Spin, Button, List, Card, Layout, Space } from 'antd';
import './index.css';
import {Simulate} from "react-dom/test-utils";
import blur = Simulate.blur;


    interface Film {
    url: string;
    title: string;
}

interface Character {
    url: string;
    name: string;
}

interface CharacterDetails {
    name: string;
    height: string;
    mass: string;
    hair_color: string;
    skin_color: string;
    eye_color: string;
    birth_year: string;
    gender: string;
}

function App() {
    const [films, setFilms] = useState<Film[]>([]);
    const [selectedFilm, setSelectedFilm] = useState<Film | null>(null);
    const [characters, setCharacters] = useState<Character[]>([]);
    const [selectedCharacter, setSelectedCharacter] = useState<Character | null>(null);
    const [characterDetails, setCharacterDetails] = useState<CharacterDetails | null>(null);
    const [loading, setLoading] = useState<boolean>(true);

    useEffect(() => {
        fetchFilms();
    }, []);

    const fetchFilms = async () => {
        try {
            const response = await axios.get<{ results: Film[] }>('/api/swapi/films');
            setFilms(response.data.results);
            setLoading(false);
        } catch (error) {
            console.error(error);
            setLoading(false);
        }
    };

    const fetchCharacters = async (filmUrl: string) => {
        try {
            const response = await axios.get<{ characters: string[] }>(filmUrl);
            const characterUrls = response.data.characters;
            const charactersData = await Promise.all(
                characterUrls.map(async (characterUrl) => {
                    const response = await axios.get<Character>(characterUrl);
                    return response.data;
                })
            );
            setCharacters(charactersData);
        } catch (error) {
            console.error(error);
        }
    };

    const fetchCharacterDetails = async (characterUrl: string) => {
        try {
            const response = await axios.get<CharacterDetails>(characterUrl);
            setCharacterDetails(response.data);
        } catch (error) {
            console.error(error);
        }
    };

    const handleFilmSelect = (film: Film) => {
        setSelectedFilm(film);
        setSelectedCharacter(null);
        setCharacterDetails(null);
        fetchCharacters(film.url);
    };

    const handleCharacterSelect = (character: Character) => {
        setSelectedCharacter(character);
        fetchCharacterDetails(character.url);
    };

    const { Header, Footer, Sider, Content } = Layout;

    const headerStyle: React.CSSProperties = {
        textAlign: 'center',
        color: '#fff',
        height: 64,
        paddingInline: 50,
        lineHeight: '64px',
        backgroundColor: 'rgb(125 188 234 / 56%)',
        display: 'block',
        position: 'fixed',
        width: '100%',
        zIndex: '20',
    };

    const contentStyle: React.CSSProperties = {
        textAlign: 'center',
        minHeight: 120,
        lineHeight: '120px',
        height: '100%',
        color: '#fff',
        backgroundColor: '#6b6b6b',
        marginTop: 150,
    };

    const siderStyle: React.CSSProperties = {
        textAlign: 'center',
        lineHeight: '120px',
        color: '#fff',
        backgroundColor: '#3ba0e9',
    };

    const footerStyle: React.CSSProperties = {
        textAlign: 'center',
        color: '#fff',
        backgroundColor: '#7dbcea',
    };

    return (
        <div>
            <Space direction="vertical" style={{ width: '100%' }} size={[0, 48]}>
                <Layout>
                    <Header className="header__pc" style={headerStyle}>
                        <h1>Фильмы из вселенной Звездные Войны (Star Wars)</h1>
                    </Header>
                    <Layout hasSider>
                        <Content style={contentStyle}>


                            {loading ? (
                                <Spin size="large" />
                            ) : (
                                <List grid={{ gutter: 16, column: 3 }}
                                      dataSource={films}
                                      renderItem={(film) => (
                                          <List.Item>
                                              <Card title={film.title} bordered={false}>
                                                  <Button onClick={() => handleFilmSelect(film)}>Select Film</Button>
                                              </Card>
                                          </List.Item>
                                      )}
                                />
                            )}

                            {selectedFilm && (
                                <div>
                                    <h2>Персонаж из {selectedFilm.title}</h2>
                                    {loading ? ( <Spin size="large" />  ) : (
                                        <List
                                            grid={{ gutter: 16, column: 3 }}
                                            dataSource={characters}
                                            renderItem={(character) => (
                                                <List.Item>
                                                    <Card title={character.name} bordered={false}>
                                                        <Button onClick={() => handleCharacterSelect(character)}>Select Character</Button>
                                                    </Card>
                                                </List.Item>
                                            )}
                                        />
                                    )}
                                </div>
                            )}

                            {selectedCharacter && characterDetails && (
                                <div className="borderCharacter">
                                    <h2>Character Details</h2>
                                    <div>Name: {characterDetails.name}</div>
                                    <div>Height: {characterDetails.height}</div>
                                    <div>Mass: {characterDetails.mass}</div>
                                    <div>Hair Color: {characterDetails.hair_color}</div>
                                    <div>Skin Color: {characterDetails.skin_color}</div>
                                    <div>Eye Color: {characterDetails.eye_color}</div>
                                    <div>Birth Year: {characterDetails.birth_year}</div>
                                    <div>Gender: {characterDetails.gender}</div>
                                </div>
                            )}
                        </Content>
                    </Layout>
                    <Footer style={footerStyle}>
                        <div>

                        </div>
                    </Footer>
                </Layout>

            </Space>



        </div>
    );
}

export default App;
