import React, { useEffect, useState } from 'react';

const Client = () => {
    const [films, setFilms] = useState<string[]>([]);

    useEffect(() => {
        const fetchFilms = async () => {
            try {
                const response = await fetch('https://swapi-graphql.netlify.app/');
                const data = await response.json();
                setFilms(data.films);
            } catch (error) {
                console.log(error);
            }
        };

        fetchFilms();
    }, []);

    const renderFilms = (films: string[]) => {
        return films.map((film, index) => <li key={index}>{film}</li>);
    };

    return (
        <div>
            <h1>Films:</h1>
            <ul>{renderFilms(films)}</ul>
        </div>
    );
};

export default Client;
